import { useState } from "react";
import axios from "axios";

function App() {
  const [city, setCity] = useState("");
  const [weather, setWeather] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const fetchWeather = async () => {
    if (!city.trim()) {
      setError("Please enter a city name.");
      return;
    }

    setLoading(true);
    setError("");
    setWeather(null);

    try {
      const response = await axios.post("http://localhost:5000/api/weather", { city });
      setWeather(response.data);
    } catch (err) {
      setError("Failed to fetch data. Check the server.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gray-100 p-4">
      <div className="bg-white shadow-lg rounded-xl p-6 w-full max-w-md">
        <h1 className="text-2xl font-bold text-center mb-4">Weather ETL</h1>

        <input
          type="text"
          placeholder="Enter city name..."
          value={city}
          onChange={(e) => setCity(e.target.value)}
          className="w-full p-2 border rounded-lg mb-3"
        />
        <button
          onClick={fetchWeather}
          disabled={loading}
          className="w-full bg-blue-500 text-white py-2 rounded-lg hover:bg-blue-600"
        >
          {loading ? "Fetching..." : "Get Weather"}
        </button>

        {error && <p className="text-red-500 text-sm mt-2">{error}</p>}

        {weather && (
          <div className="mt-4 p-4 border rounded-lg bg-gray-50">
            <h2 className="text-lg font-semibold">{weather.location_name}</h2>
            <p>Temperature: {weather.current_temp_c}Â°C</p>
            <p>Condition: {weather.current_condition_text}</p>
            <p>Updated At: {weather.updated_at}</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
